import React, { useState } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';
import { Button } from 'react-bootstrap';

function AnswerSection({ onAnswerSubmit, setAudioUrl }) {
    const [showTextInput, setShowTextInput] = useState(false);

    const handleTextSubmit = (text) => {
        onAnswerSubmit(text);
    };

    return (
        <div className="container mt-4">
            <Button variant="primary" onClick={() => setShowTextInput(!showTextInput)}>
                {showTextInput ? "切换到语音回答" : "切换到文本回答"}
            </Button>
            {showTextInput ? (
                <TextInput onTextSubmit={handleTextSubmit} />
            ) : (
                <AudioRecorder onTranscriptReady={(transcript, audioUrl) => {
                    setAudioUrl(audioUrl);
                    onAnswerSubmit(transcript);
                }} />
            )}
        </div>
    );
}

export default AnswerSection;
