import React, { useState } from 'react';
import axios from 'axios';

function GenerateSection({ question, answer, onEvaluationGenerated }) {
    const [aiChoice, setAiChoice] = useState('zhipuai');
    const [evaluation, setEvaluation] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const saveRecord = async (questionText, answerText, evaluationText) => {
        try {
            await axios.post('http://localhost:8000/api/create', { 
                question: questionText, 
                answer: answerText, 
                content: evaluationText 
            });
            // 可以在这里添加提示保存成功的逻辑
        } catch (error) {
            console.error('Error saving record:', error);
            // 可以在这里添加提示保存失败的逻辑
        }
    };

    const generateEvaluation = async () => {
        if (!answer) return; // 确保有答案再生成评价

        setIsLoading(true);
        try {
            const response = await axios.post('http://localhost:8000/api/generate', {
                prompt: answer, // 使用传入的答案
                ai: aiChoice
            });
            setEvaluation(response.data);
            if (onEvaluationGenerated) {
                onEvaluationGenerated(response.data); // 通知父组件生成的评价
            }
            // 保存答题记录
            saveRecord(question, answer, response.data); 
            setIsLoading(false);
        } catch (error) {
            console.error('Error generating evaluation:', error);
            setIsLoading(false);
        }
    };

    return (
        <div>
            <select value={aiChoice} onChange={(e) => setAiChoice(e.target.value)}>
                <option value="zhipuai">Zhipuai</option>
                <option value="openai">OpenAI</option>
                {/* 可以根据需要添加更多AI服务选项 */}
            </select>
            <button onClick={generateEvaluation} disabled={isLoading || !answer}>
                {isLoading ? '生成中...' : '生成评价'}
            </button>
            {evaluation && <div> {evaluation}</div>}
        </div>
    );
}

export default GenerateSection;
