import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import QuestionDisplay from '../components/QuestionDisplay';
import AnswerSection from '../components/AnswerSection';
import GenerateSection from '../components/GenerateSection';
import { useSettings } from '../components/SettingsContext';
import { Button, Card } from 'react-bootstrap'; // 确保这一行存在于文件顶部


function Practice() {
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [evaluation, setEvaluation] = useState('');
  const [isAnsweringAllowed, setIsAnsweringAllowed] = useState(true);
  const navigate = useNavigate();
  const { ttsService, sttService, evaluationService } = useSettings();


  useEffect(() => {
    fetchQuestion();
  }, []);

  const fetchQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/get-question');
      if (response.data) {
        setCurrentQuestion(response.data);
        setIsAnsweringAllowed(true);
      } else {
        console.error('No question received');
      }
    } catch (error) {
      console.error('Error fetching question:', error);
    }
  };

  const handleAnswerSubmit = (submittedAnswer) => {
    setAnswer(submittedAnswer);
    setIsAnsweringAllowed(false);
  };

  const handleEvaluationGenerated = (generatedEvaluation) => {
    setEvaluation(generatedEvaluation);
  };
  const handlePractice = () => {
    navigate("/practice");
  };
  // const handleReset = () => {
  //   fetchQuestion();
  // };

  // const handleViewRecords = () => {
  //   navigate('/history');
  // };


  return (
    <div className="container mt-3">
      <h1 className="text-center mb-4">练习模式</h1>        
      <Button variant="primary" className="mr-2" onClick={handlePractice}>继续出题</Button>

      <div className='h4'><strong><QuestionDisplay question={currentQuestion.question} ttsService={ttsService} /> </strong></div>
      <AnswerSection onAnswerSubmit={handleAnswerSubmit} isAnsweringAllowed={isAnsweringAllowed} sttService={sttService} />
      {answer && !isAnsweringAllowed && <GenerateSection question={currentQuestion.question} answer={answer} onEvaluationGenerated={handleEvaluationGenerated} evaluationService={evaluationService} />}
      <div className="d-flex justify-content-between mt-3">
      </div>
      {evaluation && (
        <div className="mt-3">
          <Card>
          <Card.Header >
              <strong >question</strong> 
            </Card.Header>
            <Card.Body>
            <Card.Subtitle className="mb-2 " style={{ textAlign: 'left'}}>你的回答:{answer}</Card.Subtitle>
            <Card.Text style={{   whiteSpace: 'pre-line',textAlign: 'left' }}>

            <strong>评价:</strong>          <p>{evaluation}</p>
          </Card.Text>
          </Card.Body>
          </Card>
        </div>
      )}
    </div>
  );
}

export default Practice;
