// AnswerSection.jsx
import React, { useState, useRef, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import axios from 'axios';
import '../App.css';

function AnswerSection({ onAnswerSubmit, disabled }) {
    const [isRecording, setIsRecording] = useState(true);
    const [inputText, setInputText] = useState('');
    const [audioUrl, setAudioUrl] = useState(null);
    const [transcript, setTranscript] = useState('');
    const [countdown, setCountdown] = useState(20);
    const [error, setError] = useState(null);
    const mediaRecorderRef = useRef(null);
    const countdownTimerRef = useRef(null);

    // 切换回答模式
    const toggleAnswerMode = () => {
        setIsRecording(!isRecording);
        if (!isRecording) {
            onAnswerSubmit(inputText); // 如果切换到录音模式，提交文本答案
        }
    };

    // 开始录音
    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            mediaRecorderRef.current = new MediaRecorder(stream);
            const audioChunks = [];

            mediaRecorderRef.current.ondataavailable = event => {
                audioChunks.push(event.data);
            };

            mediaRecorderRef.current.onstop = async () => {
                if (!audioChunks.length) {
                    setError('No audio data available.');
                    return;
                }
                const audioBlob = new Blob(audioChunks, { type: 'audio/mpeg' });
                setAudioUrl(URL.createObjectURL(audioBlob));
                uploadAudio(audioBlob);
            };

            mediaRecorderRef.current.start();
            setIsRecording(true);
            setCountdown(20);
            countdownTimerRef.current = setInterval(() => {
                setCountdown(prevCountdown => prevCountdown - 1);
            }, 1000);
        } catch (error) {
            setError('Error accessing media devices');
        }
    };

    // 停止录音
    const stopRecording = () => {
        if (mediaRecorderRef.current && isRecording) {
            mediaRecorderRef.current.stop();
            setIsRecording(false);
            clearInterval(countdownTimerRef.current);
        }
    };
    // 倒计时结束停止录音
    useEffect(() => {
        if (countdown === 0 && isRecording) {
            stopRecording();
        }
    }, [countdown, isRecording]);

    // 上传音频并获取转写
    const uploadAudio = async (audioBlob) => {
        const formData = new FormData();
        formData.append("audioFile", audioBlob, "audio.mp3");

        try {
            const response = await axios.post('http://localhost:8000/api/upload-audio', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setTranscript(response.data.transcript);
            onAnswerSubmit(response.data.transcript);
        } catch (error) {
            console.error('Error uploading audio:', error);
        }
    };

    // 当录音完成时，处理转录文本并提交答案
    useEffect(() => {
        if (transcript) {
            onAnswerSubmit(transcript);
        }
    }, [transcript, onAnswerSubmit]);

    // 清除倒计时
    useEffect(() => {
        return () => {
            clearInterval(countdownTimerRef.current);
        };
    }, []);


    const handleAudioTranscriptReady = (transcript, audioUrl) => {
        //   setIsRecording(false); // 停止录音
          setTranscript(transcript); // 设置转写文本
          setAudioUrl(audioUrl); // 设置录音文件的 URL
          onAnswerSubmit(transcript); // 提交答案进行评价
      };


    return (
        <div className="container mt-4">
            <Button variant="primary" onClick={toggleAnswerMode} disabled={disabled}>
                <i className={isRecording ? "fa-solid fa-keyboard" : "fa-solid fa-microphone"}></i>
                {isRecording ? '切换到文本' : '切换到语音'}
            </Button>

            {isRecording ? (
                <div>
                    <div className="my-3">
                        <strong className="text-primary">限时回答 : {countdown} 秒</strong>
                    </div>
                    <div>
                        {isRecording ? (
                            <Button className="btn-danger my-1" onClick={stopRecording} disabled={countdown === 0}>
                                <i className="fa-regular fa-circle-stop fa-lg"></i> 停止录音
                            </Button>
                        ) : (
                            <Button className="btn-success my-1" onClick={startRecording}>
                                <i className="fa-solid fa-microphone fa-lg"></i> 开始录音
                            </Button>
                        )}
                        {error && <p className="text-danger">Error: {error}</p>}
                        {audioUrl && <audio src={audioUrl} controls />}
                    </div>
                </div>
            ) : (
                <div className="textarea-container my-3">
                    <textarea
                        value={inputText}
                        onChange={e => setInputText(e.target.value)}
                        className="form-control"
                        rows="4"
                        placeholder="输入内容"
                    />
                </div>
            )}
        </div>
    );
}

export default AnswerSection;
