// AnswerSection-old.jsx
import React, { useState, useRef, useEffect } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';
import axios from 'axios';
import { Button, Spinner } from 'react-bootstrap';
import '../App.css';

function AnswerSection({ onAnswerSubmit,disabled,onTranscriptReady }) {

  const [isRecording, setIsRecording] = useState(false);
  const [audioUrl, setAudioUrl] = useState(null); // ÂÆö‰πâ audioUrl Áä∂ÊÄÅ
  const [transcript, setTranscript] = useState(''); // ÂÆö‰πâ transcript Áä∂ÊÄÅ

  const [countdown, setCountdown] = useState(20);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isRecordingMode, setIsRecordingMode] = useState(true); // ÈªòËÆ§‰∏∫ÂΩïÈü≥Ê®°Âºè
  const countdownTimerRef = useRef(null);
  const mediaRecorderRef = useRef(null);

  const [showTextInput, setShowTextInput] = useState(false);
  // const [answer, setAnswer] = useState(''); // Ê≠£Á°ÆÂ£∞Êòé answer Áä∂ÊÄÅÂíå setAnswer ÂáΩÊï∞

    const handleAudioTranscriptReady = (transcript, audioUrl) => {
    //   setIsRecording(false); // ÂÅúÊ≠¢ÂΩïÈü≥
      setTranscript(transcript); // ËÆæÁΩÆËΩ¨ÂÜôÊñáÊú¨
      setAudioUrl(audioUrl); // ËÆæÁΩÆÂΩïÈü≥Êñá‰ª∂ÁöÑ URL
      onAnswerSubmit(transcript); // Êèê‰∫§Á≠îÊ°àËøõË°åËØÑ‰ª∑
  };

    // const toggleAnswerMode = () => {
    //     setIsRecording(!isRecording);
    // };

    const toggleAnswerMode = () => {
      setIsRecordingMode(!isRecordingMode);
      // Ê∏ÖÈô§ÊñáÊú¨ÊàñËØ≠Èü≥ÁöÑÁä∂ÊÄÅ
      setAudioUrl(null);
      setTranscript('');
      setError(null);
  };

    useEffect(() => {
      if (countdown === 0 && isRecording) {
          stopRecording();
      }
  }, [countdown, isRecording]);

  useEffect(() => {
      return () => {
          clearInterval(countdownTimerRef.current);
      };
  }, []);

  const startRecording = async () => {
      // Ê∏ÖÈô§ÊóßÁöÑÂΩïÈü≥Êñá‰ª∂ÂíåËΩ¨ÂÜôÊñáÊú¨
      setAudioUrl(null);
      setTranscript('');
      setError(null);

      try {

          const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
          mediaRecorderRef.current = new MediaRecorder(stream);
          const audioChunks = [];

          mediaRecorderRef.current.ondataavailable = event => {
              audioChunks.push(event.data);
          };

          mediaRecorderRef.current.onstop = async () => {
              if (!audioChunks.length) {
                  setError('No audio data available.');
                  return;
              }
              const audioBlob = new Blob(audioChunks, { type: 'audio/mpeg' });
              setAudioUrl(URL.createObjectURL(audioBlob));
              uploadAudio(audioBlob);
          };

          mediaRecorderRef.current.start();
          setIsRecording(true);
          setCountdown(20); // ÈáçÁΩÆÂÄíËÆ°Êó∂
          countdownTimerRef.current = setInterval(() => {
              setCountdown(prevCountdown => prevCountdown - 1);
          }, 1000);
      } catch (error) {
          setError('Error accessing media devices');
      }
  };

  const stopRecording = () => {
      if (mediaRecorderRef.current && isRecording) {
          mediaRecorderRef.current.stop();
          setIsRecording(false);
          clearInterval(countdownTimerRef.current);
      }
  };

  const uploadAudio = async (audioBlob) => {
      setLoading(true); // ÂºÄÂßã‰∏ä‰º†Êó∂ÊòæÁ§∫ Spinner
      const formData = new FormData();
      formData.append("audioFile", audioBlob, "audio.mp3");

      try {
          const response = await axios.post('http://localhost:8000/api/upload-audio', formData, {
              headers: {
                  'Content-Type': 'multipart/form-data',
              },
          });
          setTranscript(response.data.transcript);
          // onTranscriptReady(response.data.transcript);
          onAnswerSubmit(response.data.transcript); // Êõ¥Êñ∞Á≠îÊ°àÁä∂ÊÄÅ

      } catch (error) {
          console.error('Error uploading audio:', error);
      }
      setLoading(false); // ‰∏ä‰º†ÁªìÊùüÊàñÂèëÁîüÈîôËØØÊó∂ÈöêËóè Spinner

  };



    return (
        <div id="answerarea" className="container mt-4">
            {isRecordingMode ? (
                <div>
                    <div className="my-3">
                        <strong className="text-primary">
                            <i className="fa-solid fa-hourglass-start"></i> ÈôêÊó∂ÂõûÁ≠î : {countdown} Áßí
                        </strong>
                    </div>
                    <div>
                        {transcript && <p>üíÅ‚Äç‚ôÇÔ∏è: {transcript}</p>}
                        {error && <p>Error: {error}</p>}
                        {loading && <Spinner animation="border" variant="primary" role="status">
                            <span className="sr-only">Loading...</span>
                        </Spinner>}<br/>
                        {audioUrl && <audio src={audioUrl} controls />}
                    </div>
                    <Button 
                        className="btn-danger my-1" 
                        onClick={isRecording ? stopRecording : startRecording}
                        disabled={isRecording && countdown === 0}
                    >
                        <i className={`fa-${isRecording ? 'regular fa-circle-stop' : 'solid fa-microphone'} fa-lg`}></i>
                    </Button>
                </div>
            ) : (
                <TextInput onTextSubmit={onAnswerSubmit} />
            )}
            <Button variant="primary" onClick={toggleAnswerMode} disabled={disabled}>
                <i className={isRecordingMode ? "fa-solid fa-keyboard" : "fa-solid fa-microphone"}></i>
                {isRecordingMode ? ' ÊñáÊú¨' : ' ÂàáÊç¢Âà∞ËØ≠Èü≥Ê®°Âºè'}
            </Button>
        </div>
    );
}
export default AnswerSection;
