// AnswerSection.jsx
import React, { useState, useRef, useEffect } from 'react';
import { Button } from 'react-bootstrap';
import axios from 'axios';
import '../App.css';

function AnswerSection({ onAnswerSubmit, disabled }) {
    const [isRecording, setIsRecording] = useState(true);
    const [inputText, setInputText] = useState('');
    const [audioUrl, setAudioUrl] = useState(null);
    const [transcript, setTranscript] = useState('');
    const [countdown, setCountdown] = useState(20);
    const [error, setError] = useState(null);
    const mediaRecorderRef = useRef(null);
    const countdownTimerRef = useRef(null);

    // ÂàáÊç¢ÂõûÁ≠îÊ®°Âºè
    const toggleAnswerMode = () => {
        setIsRecording(!isRecording);
        if (!isRecording) {
            onAnswerSubmit(inputText); // Â¶ÇÊûúÂàáÊç¢Âà∞ÂΩïÈü≥Ê®°ÂºèÔºåÊèê‰∫§ÊñáÊú¨Á≠îÊ°à
        }
    };

    // ÂºÄÂßãÂΩïÈü≥
    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            mediaRecorderRef.current = new MediaRecorder(stream);
            const audioChunks = [];

            mediaRecorderRef.current.ondataavailable = event => {
                audioChunks.push(event.data);
            };

            mediaRecorderRef.current.onstop = async () => {
                if (!audioChunks.length) {
                    setError('No audio data available.');
                    return;
                }
                const audioBlob = new Blob(audioChunks, { type: 'audio/mpeg' });
                setAudioUrl(URL.createObjectURL(audioBlob));
                uploadAudio(audioBlob);
            };

            mediaRecorderRef.current.start();
            setIsRecording(true);
            setCountdown(20);
            countdownTimerRef.current = setInterval(() => {
                setCountdown(prevCountdown => prevCountdown - 1);
            }, 1000);
        } catch (error) {
            setError('Error accessing media devices');
        }
    };

    // ÂÅúÊ≠¢ÂΩïÈü≥
    const stopRecording = () => {
        if (mediaRecorderRef.current && isRecording) {
            mediaRecorderRef.current.stop();
            setIsRecording(false);
            clearInterval(countdownTimerRef.current);
        }
    };

    // ‰∏ä‰º†Èü≥È¢ëÂπ∂Ëé∑ÂèñËΩ¨ÂÜô
    const uploadAudio = async (audioBlob) => {
        const formData = new FormData();
        formData.append("audioFile", audioBlob, "audio.mp3");

        try {
            const response = await axios.post('http://localhost:8000/api/upload-audio', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setTranscript(response.data.transcript);
            onAnswerSubmit(response.data.transcript);
        } catch (error) {
            console.error('Error uploading audio:', error);
        }
    };

    // ÂΩìÂΩïÈü≥ÂÆåÊàêÊó∂ÔºåÂ§ÑÁêÜËΩ¨ÂΩïÊñáÊú¨Âπ∂Êèê‰∫§Á≠îÊ°à
    useEffect(() => {
        if (transcript) {
            onAnswerSubmit(transcript);
        }
    }, [transcript, onAnswerSubmit]);

    // Ê∏ÖÈô§ÂÄíËÆ°Êó∂
    useEffect(() => {
        return () => {
            clearInterval(countdownTimerRef.current);
        };
    }, []);

    return (
        <div id="answerarea" className="container mt-4">
            <Button variant="primary" onClick={toggleAnswerMode} disabled={disabled}>
                <i className={isRecording ? "fa-solid fa-keyboard" : "fa-solid fa-microphone"}></i>
                {isRecording ? 'ÂàáÊç¢Âà∞ÊñáÊú¨' : 'ÂàáÊç¢Âà∞ËØ≠Èü≥'}
            </Button>
            {isRecording ? (
                <div>
                    <div className="my-3">
                        <strong className="text-primary">ÈôêÊó∂ÂõûÁ≠î : {countdown} Áßí</strong>
                    </div>
                    {isRecording ? (
                        <Button className="btn-danger my-1" onClick={stopRecording} disabled={countdown === 0}><i class="fa-regular fa-circle-stop fa-lg"></i></Button>
                    ) : (
                        <Button className="btn-danger my-1" onClick={startRecording}><i class="fa-solid fa-microphone fa-lg"></i></Button>
                    )}
                    {transcript && <p>üíÅ‚Äç‚ôÇÔ∏è: {transcript}</p>}
                    {error && <p>Error: {error}</p>}
                    {audioUrl && <audio src={audioUrl} controls />}
                </div>
            ) : (
                <div className="textarea-container my-3">
                    <textarea
                        value={inputText}
                        onChange={e => setInputText(e.target.value)}
                        className="form-control"
                        rows="4"
                        placeholder="ËæìÂÖ•ÂÜÖÂÆπ"
                    />
                </div>
            )}
        </div>
    );
}

export default AnswerSection;
