import React, { useState } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';
import { Button } from 'react-bootstrap';

function AnswerSection({ onAnswerSubmit }) {
    const [transcript, setTranscript] = useState('');
    const [audioUrl, setAudioUrl] = useState('');
    const [isRecording, setIsRecording] = useState(false);
    const [showTextInput, setShowTextInput] = useState(false);

    const handleAudioTranscriptReady = (transcriptText, audioUrlData) => {
        setIsRecording(false);
        setTranscript(transcriptText);
        setAudioUrl(audioUrlData);
        onAnswerSubmit(transcriptText);
    };

    const toggleRecording = () => {
        setIsRecording(!isRecording);
        setShowTextInput(false);
    };

    const switchToTextInput = () => {
        setIsRecording(false);
        setShowTextInput(true);
    };

    return (
        <div className="container mt-4">
            <Button variant={isRecording ? "warning" : "danger"} onClick={toggleRecording}>
                <i className={`fa-solid ${isRecording ? "fa-square" : "fa-microphone"}`}></i>
                {isRecording ? " 停止录音" : " 语音回答"}
            </Button>
            <Button variant="primary" onClick={switchToTextInput}><i className="fa-solid fa-keyboard"></i> 文本回答</Button>
            {isRecording && <AudioRecorder onTranscriptReady={handleAudioTranscriptReady} autoStart={isRecording} />}
            {showTextInput && <TextInput onTextSubmit={onAnswerSubmit} />}
            {audioUrl && <audio src={audioUrl} controls />}
            {transcript && <p>{transcript}</p>}
        </div>
    );
}

export default AnswerSection;
