import React, { useState, useEffect } from 'react';
import QuestionDisplay from './components/QuestionDisplay';
import AnswerSection from './components/AnswerSection';
import GenerateSection from './components/GenerateSection';
import axios from 'axios';

function InterviewPage() {
    const [currentQuestion, setCurrentQuestion] = useState('');
    const [userAnswer, setUserAnswer] = useState('');
    const [evaluation, setEvaluation] = useState('');
    const [audioUrl, setAudioUrl] = useState('');

    useEffect(() => {
        // 从后端获取题目
        fetchQuestion();
    }, []);

    const fetchQuestion = async () => {
        try {
            // 假设后端有一个API端点 '/api/question'
            const response = await axios.get('http://localhost:8000/api/question');
            setCurrentQuestion(response.data.question);
        } catch (error) {
            console.error('Error fetching question:', error);
        }
    };

    const handleAnswerSubmit = (answer) => {
        setUserAnswer(answer);
    };

    const handleEvaluationGenerated = (evaluationText) => {
        setEvaluation(evaluationText);
    };

    const saveRecord = async () => {
        try {
            // 假设后端有一个API端点 '/api/save-record'
            await axios.post('http://localhost:8000/api/save-record', {
                question: currentQuestion,
                answer: userAnswer,
                evaluation: evaluation,
                audioUrl: audioUrl
            });
            // 重置页面以准备下一个问题
            fetchQuestion();
            setUserAnswer('');
            setEvaluation('');
            setAudioUrl('');
        } catch (error) {
            console.error('Error saving record:', error);
        }
    };

    return (
        <div>
            <QuestionDisplay question={currentQuestion} />
            <AnswerSection onAnswerSubmit={handleAnswerSubmit} setAudioUrl={setAudioUrl} />
            {userAnswer && (
                <GenerateSection
                    question={currentQuestion}
                    answer={userAnswer}
                    onEvaluationGenerated={handleEvaluationGenerated}
                />
            )}
            {evaluation && (
                <div>
                    <p>评价: {evaluation}</p>
                    <button onClick={saveRecord}>保存记录</button>
                </div>
            )}
        </div>
    );
}

export default InterviewPage;
