// Practice.jsx
import React, { useState } from 'react';
import axios from 'axios';
// import { useNavigate } from 'react-router-dom';
import QuestionDisplay from '../components/QuestionDisplay';
import AnswerSection from '../components/AnswerSection';
import GenerateSection from '../components/GenerateSection';
import { useSettings } from '../components/SettingsContext';
import { Button, Card } from 'react-bootstrap'; // 确保这一行存在于文件顶部


function Practice() {
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [evaluation, setEvaluation] = useState('');
  const [isAnsweringAllowed, setIsAnsweringAllowed] = useState(true);
  // const navigate = useNavigate();
  const { ttsService, sttService, evaluationService } = useSettings();
  const [audioUrl, setAudioUrl] = useState(''); // 新增状态以存储音频 URL
  const [isEvaluationGenerated, setIsEvaluationGenerated] = useState(false);
  const [isQuestionGenerated, setIsQuestionGenerated] = useState(false);
  const [questionCount, setQuestionCount] = useState(0);
  const [resetFlag, setResetFlag] = useState(false);


    // 获取问题的函数
    const fetchAndPlayQuestion = async () => {
      try {
          const response = await axios.get('http://localhost:8000/api/get-question');
          if (response.data) {
              setCurrentQuestion(response.data);
              setIsAnsweringAllowed(true);
          } else {
              console.error('No question received');
          }
      } catch (error) {
          console.error('Error fetching question:', error);
      }
      setIsEvaluationGenerated(false); // 重置生成评价状态
      setAnswer(''); // 清空答案
      setAudioUrl(''); // 清空录音 URL

      setEvaluation(''); // 清空评价
      setIsQuestionGenerated(true);
      setResetFlag(!resetFlag); // 切换重置标志

  };


  const handleAnswerSubmit = (submittedAnswer) => {
    setAnswer(submittedAnswer);
    setIsAnsweringAllowed(false);
  };


  const handleEvaluationGenerated = (generatedEvaluation) => {
    setEvaluation(generatedEvaluation);
    setIsEvaluationGenerated(true); // 设置生成评价后的状态
    setQuestionCount(questionCount + 1); // 递增答题计数器
  };



  const handleReset = () => {
    fetchAndPlayQuestion(); // 使用新的问题获取函数
};


const endPractice = () => {
  // 弹出统计信息
  alert(`恭喜您完成练习！您总共回答了 ${questionCount} 道题目。`);
  // 重置状态，如果需要
  setQuestionCount(0);
  setIsQuestionGenerated(false);

};

  return (
    <div className="container mt-3">
    <h1 className="text-center mb-4">练习模式</h1>
    <Button variant="primary" onClick={fetchAndPlayQuestion}>
        {isQuestionGenerated ? '下一题' : '生成问题'}
      </Button>
      {isQuestionGenerated && (
        <Button variant="secondary" onClick={endPractice}>
          结束练习
        </Button>
      )}

      {currentQuestion.question && (
      <>
        <QuestionDisplay question={currentQuestion.question} ttsService={ttsService} />
        <AnswerSection onAnswerSubmit={handleAnswerSubmit} 
        resetFlag={resetFlag} // 将重置标志作为 prop 传递
        setAudioUrl={setAudioUrl}         
        disabled={isEvaluationGenerated} // 禁用答题方式切换
      />
        <GenerateSection 
          question={currentQuestion.question} 
          answer={answer} 
          onEvaluationGenerated={handleEvaluationGenerated} 
          evaluationService={evaluationService} 
          disabled={isEvaluationGenerated} // 禁用生成评价按钮

      />
        </>
    )}
      {evaluation && (
        <div className="mt-3">
          <Card>
          <Card.Header >
              <strong >{currentQuestion.question} </strong> 
            </Card.Header>
            <Card.Body>
            <Card.Subtitle className="mb-2 " style={{ textAlign: 'left'}}>你的回答:{answer}</Card.Subtitle>
            <Card.Text style={{   whiteSpace: 'pre-line',textAlign: 'left' }}>

            <strong>评价:</strong>         
             {evaluation}
          </Card.Text>
          </Card.Body>
          </Card>
        </div>
      )}          

    </div>
  );
}

export default Practice;
