          .then(response => {
            const audioUrl = URL.createObjectURL(response.data);
            if (!audioRef.current) {
              audioRef.current = new Audio(audioUrl);
            }
            audioRef.current.play();
          })
          .catch(error => console.error('Error with TTS:', error));
      }
    };

