import React, { useState } from 'react';
import axios from 'axios';

function GenerateSection({ question, answer, onEvaluationGenerated }) {
    const [evaluation, setEvaluation] = useState('');
    const [isLoading, setIsLoading] = useState(false);

    const generateEvaluation = async () => {
        if (!answer) return; // 确保有答案再生成评价

        setIsLoading(true);
        try {
            const response = await axios.post('http://localhost:8000/api/generate', {
                prompt: answer, // 使用传入的答案
                ai: aiChoice
            });
            setEvaluation(response.data);
            if (onEvaluationGenerated) {
                onEvaluationGenerated(response.data); // 通知父组件生成的评价
            }
            // 保存答题记录
            saveRecord(question, answer, response.data); 
            setIsLoading(false);
        } catch (error) {
            console.error('Error generating evaluation:', error);
            setIsLoading(false);
        }
    };

    return (
        <div>
            <button onClick={generateEvaluation} disabled={isLoading || !answer}>
                {isLoading ? '生成中...' : '生成评价'}
            </button>
            {evaluation && <div>{evaluation}</div>}
        </div>
    );
}

export default GenerateSection;
