// AnswerSection-old.jsx
import React, { useState, useRef, useEffect } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';
import axios from 'axios';
import { Button, Spinner } from 'react-bootstrap';
import '../App.css';

function AnswerSection({ onAnswerSubmit,disabled,onTranscriptReady }) {

  const [isRecording, setIsRecording] = useState(false);
  const [countdown, setCountdown] = useState(20);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [transcript, setTranscript] = useState(''); // 定义 transcript 状态
  const [audioUrl, setAudioUrl] = useState(''); // 定义 audioUrl 状态
  const countdownTimerRef = useRef(null);
  const mediaRecorderRef = useRef(null);

  const [showTextInput, setShowTextInput] = useState(false);
  // const [answer, setAnswer] = useState(''); // 正确声明 answer 状态和 setAnswer 函数

    const handleAudioTranscriptReady = (transcript, audioUrl) => {
    //   setIsRecording(false); // 停止录音
      setTranscript(transcript); // 设置转写文本
      setAudioUrl(audioUrl); // 设置录音文件的 URL
      onAnswerSubmit(transcript); // 提交答案进行评价
  };

    const toggleAnswerMode = () => {
        setIsRecording(!isRecording);
    };
    useEffect(() => {
      if (countdown === 0 && isRecording) {
          stopRecording();
      }
  }, [countdown, isRecording]);

  useEffect(() => {
      return () => {
          clearInterval(countdownTimerRef.current);
      };
  }, []);

  const startRecording = async () => {
      // 清除旧的录音文件和转写文本
      setAudioUrl(null);
      setTranscript('');
      setError(null);

      try {

          const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
          mediaRecorderRef.current = new MediaRecorder(stream);
          const audioChunks = [];

          mediaRecorderRef.current.ondataavailable = event => {
              audioChunks.push(event.data);
          };

          mediaRecorderRef.current.onstop = async () => {
              if (!audioChunks.length) {
                  setError('No audio data available.');
                  return;
              }
              const audioBlob = new Blob(audioChunks, { type: 'audio/mpeg' });
              setAudioUrl(URL.createObjectURL(audioBlob));
              uploadAudio(audioBlob);
          };

          mediaRecorderRef.current.start();
          setIsRecording(true);
          setCountdown(20); // 重置倒计时
          countdownTimerRef.current = setInterval(() => {
              setCountdown(prevCountdown => prevCountdown - 1);
          }, 1000);
      } catch (error) {
          setError('Error accessing media devices');
      }
  };

  const stopRecording = () => {
      if (mediaRecorderRef.current && isRecording) {
          mediaRecorderRef.current.stop();
          setIsRecording(false);
          clearInterval(countdownTimerRef.current);
      }
  };

  const uploadAudio = async (audioBlob) => {
      setLoading(true); // 开始上传时显示 Spinner
      const formData = new FormData();
      formData.append("audioFile", audioBlob, "audio.mp3");

      try {
          const response = await axios.post('http://localhost:8000/api/upload-audio', formData, {
              headers: {
                  'Content-Type': 'multipart/form-data',
              },
          });
          setTranscript(response.data.transcript);
          onTranscriptReady(response.data.transcript);
      } catch (error) {
          console.error('Error uploading audio:', error);
      }
      setLoading(false); // 上传结束或发生错误时隐藏 Spinner

  };


    return (

        <div id="answerarea" className="container  mt-4">
            {transcript && !isRecording && (
            <>
              <audio src={audioUrl} controls />
              <p>{transcript}</p>
            </>
          )}
          {isRecording ? (
            <AudioRecorder onTranscriptReady={handleAudioTranscriptReady} autoStart={isRecording} />
          ) : (
            <TextInput onTextSubmit={onAnswerSubmit} />
            )}

          <Button variant="primary" onClick={toggleAnswerMode}  disabled={disabled}> {/* 使用 disabled 属性 */}
            <i className={isRecording ? "fa-solid fa-keyboard" : ""}></i>
            {isRecording ? '' : '取消'}
          </Button>
        </div>
    );
}
export default AnswerSection;
