import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import QuestionDisplay from '../components/QuestionDisplay';
import AnswerSection from '../components/AnswerSection';
import GenerateSection from '../components/GenerateSection';
import ControlPanel from '../components/ControlPanel'; // 新添加的组件
import { Button, Modal } from 'react-bootstrap'; // 引入Bootstrap组件

function Practice() {
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [evaluation, setEvaluation] = useState('');
  const [isAnsweringAllowed, setIsAnsweringAllowed] = useState(true);
  const [ttsService, setTtsService] = useState('openai'); // 默认使用 OpenAI TTS
  const [sttService, setSttService] = useState('openai'); // 默认使用 OpenAI STT
  const [evaluationService, setEvaluationService] = useState('zhipuai'); // 默认使用 Zhipuai 评价

  const navigate = useNavigate(); 

  useEffect(() => {
    fetchQuestion();
  }, []);

  const fetchQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/get-question');
      if (response.data) {
        setCurrentQuestion(response.data);
      } else {
        console.error('No question received');
      }
    } catch (error) {
      console.error('Error fetching question:', error);
    }
  };

  const handleAnswerSubmit = (submittedAnswer) => {
    setAnswer(submittedAnswer);
    setEvaluation('');
    setIsAnsweringAllowed(false);
  };

  const handleEvaluationGenerated = (generatedEvaluation) => {
    setEvaluation(generatedEvaluation);
  };

  const handleReset = () => {
    setIsAnsweringAllowed(true);
    setAnswer('');
    setEvaluation('');
  };

  const handleContinue = async () => {
    setIsAnsweringAllowed(true);
    setAnswer('');
    setEvaluation('');
    await fetchQuestion();
  };

  const handleViewRecords = () => {
    navigate('/history');
  };

  return (
    <div className="bg-light">
      <h1 className="display-4">练习模式</h1>
      <ControlPanel setTtsService={setTtsService} setSttService={setSttService} setEvaluationService={setEvaluationService} />
      <QuestionDisplay question={currentQuestion.question} ttsService={ttsService} />
      <AnswerSection onAnswerSubmit={handleAnswerSubmit} isAnsweringAllowed={isAnsweringAllowed} reset={handleReset} sttService={sttService} />
      {answer && <GenerateSection question={currentQuestion.question} answer={answer} onEvaluationGenerated={handleEvaluationGenerated} evaluationService={evaluationService} />}
      <div>
        <Button onClick={handleReset} variant="outline-secondary">重新练习</Button>
        <Button onClick={handleContinue} variant="primary">继续答题</Button>
        <Button onClick={handleViewRecords} variant="info">查看记录</Button>
      </div>
    </div>
  );
}

export default Practice;
