// frontend/src/components/QuestionDisplay.jsx
import React, { useEffect,useRef } from 'react';
import axios from 'axios';

function QuestionDisplay({ question, ttsService, stopAudio }) {
    const audioRef = useRef(null);
    const { isMuted } = useSettings(); // 获取静音模式状态


    useEffect(() => {
        if (question && !isMuted) {
            playAudio(question);
        }

        // 清理函数，在组件卸载时停止音频播放
        return () => {
            if (audioRef.current) {
            audioRef.current.pause();
            }
            speechSynthesis.cancel();
        };
        }, [question]);

    const playAudio = (text) => {
        switch (ttsService) {
            case 'browser':
                const utterance = new SpeechSynthesisUtterance(text);
                speechSynthesis.speak(utterance);
                break;
            case 'openai':
                axios.post('http://localhost:8000/api/text-to-speech', { text }, { responseType: 'blob' })
                    .then(response => playAudioBlob(response.data))
                    .catch(error => console.error('Error with OpenAI TTS:', error));
                break;
            case 'minimax':
                axios.post('http://localhost:8000/api/text-to-speech/minimax', { text }, { responseType: 'blob' })
                    .then(response => playAudioBlob(response.data))
                    .catch(error => console.error('Error with Minimax TTS:', error));
                break;
            default:
                console.error('Unknown TTS service:', ttsService);
        }


    if (ttsService === 'browser') {
        const utterance = new SpeechSynthesisUtterance(text);
        speechSynthesis.speak(utterance);
      } else {
        // 外部API音频播放逻辑
        axios.post('...', { text }, { responseType: 'blob' })
          .then(response => {
            const audioUrl = URL.createObjectURL(response.data);
            if (!audioRef.current) {
              audioRef.current = new Audio(audioUrl);
            }
            audioRef.current.play();
          })
          .catch(error => console.error('Error with TTS:', error));
      }
    };

    const playAudioBlob = (blob) => {
        const audioUrl = URL.createObjectURL(blob);
        const audio = new Audio(audioUrl);
        audio.play();
    };



    // 将此方法暴露给父组件，以便在其他操作时调用
    useEffect(() => {
        if (stopAudio) {
        stopAudio.current = () => {
            if (audioRef.current) {
            audioRef.current.pause();
            }
            speechSynthesis.cancel();
        };
        }
    }, [stopAudio]);


    return (

        <div>

            {question || "没有问题"}
        </div>
    );
}

export default QuestionDisplay;
