import React, { useState } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';
import GenerateSection from './GenerateSection'; // 确保引入 GenerateSection 组件
import { Button } from 'react-bootstrap';

function AnswerSection({ onAnswerSubmit, setAudioUrl }) {
    const [answerMode, setAnswerMode] = useState('none'); // 'none', 'audio', 'text'
    const [transcript, setTranscript] = useState('');

    const handleAudioTranscriptReady = (transcriptText, audioUrlData) => {
        setTranscript(transcriptText);
        setAudioUrl(audioUrlData);
        onAnswerSubmit(transcriptText);
        setAnswerMode('none'); // 完成录音后重置模式
    };

    const switchToAudioInput = () => {
        setAnswerMode(answerMode === 'audio' ? 'none' : 'audio');
    };

    const switchToTextInput = () => {
        setAnswerMode(answerMode === 'text' ? 'none' : 'text');
    };

    return (
        <div className="container mt-4">
            <Button variant="primary" onClick={switchToAudioInput}>
                <i className="fa-solid fa-microphone"></i> 录音回答
            </Button>
            <Button variant="primary" onClick={switchToTextInput}>
                <i className="fa-solid fa-keyboard"></i> 文本回答
            </Button>

            {answerMode === 'audio' && (
                <AudioRecorder onTranscriptReady={handleAudioTranscriptReady} />
            )}
            {answerMode === 'text' && (
                <TextInput onTextSubmit={onAnswerSubmit} />
            )}

            {/* 在此处加入 GenerateSection 组件 */}
            <GenerateSection answer={transcript} />
        </div>
    );
}

export default AnswerSection;
