// AudioRecorder.jsx
import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css'; // 导入 Bootstrap 样式


function AudioRecorder({ onTranscriptReady, autoStart }) {
    const [audioUrl, setAudioUrl] = useState(null);
    const [transcript, setTranscript] = useState('');
    const [countdown, setCountdown] = useState(20);
    const [error, setError] = useState(null);

    const mediaRecorderRef = useRef(null);
    const countdownTimerRef = useRef(null);
        // 处理录音的自动开始和停止


    useEffect(() => {
        if (autoStart) {
            startRecording();
        } else if (mediaRecorderRef.current && mediaRecorderRef.current.state === "recording") {
            stopRecording();
        }
    }, [autoStart]);

    useEffect(() => {
        if (countdown === 0 && isRecording) {
            stopRecording();
        }
    }, [countdown, isRecording]);

    useEffect(() => {
        return () => {
            clearInterval(countdownTimerRef.current);
        };
    }, []);

    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            mediaRecorderRef.current = new MediaRecorder(stream);
            const audioChunks = [];

            mediaRecorderRef.current.ondataavailable = event => {
                audioChunks.push(event.data);
            };

            mediaRecorderRef.current.onstop = async () => {
                if (!audioChunks.length) {
                    setError('No audio data available.');
                    return;
                }
                const audioBlob = new Blob(audioChunks, { type: 'audio/mpeg' });
                setAudioUrl(URL.createObjectURL(audioBlob));
                uploadAudio(audioBlob);
            };

            mediaRecorderRef.current.start();
            setIsRecording(true);
            setCountdown(20); // 重置倒计时
            countdownTimerRef.current = setInterval(() => {
                setCountdown(prevCountdown => prevCountdown - 1);
            }, 1000);
        } catch (error) {
            setError('Error accessing media devices');
        }
    };

    const stopRecording = () => {
        if (mediaRecorderRef.current && isRecording) {
            mediaRecorderRef.current.stop();
            setIsRecording(false);
            clearInterval(countdownTimerRef.current);
        }
    };

    const uploadAudio = async (audioBlob) => {
        const formData = new FormData();
        formData.append("audioFile", audioBlob, "audio.mp3");

        try {
            const response = await axios.post('http://localhost:8000/api/upload-audio', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setTranscript(response.data.transcript);
            onTranscriptReady(response.data.transcript);
        } catch (error) {
            console.error('Error uploading audio:', error);
        }
    };

  // 添加录音结束的反馈
  useEffect(() => {
    if (countdown === 0 && isRecording) {
      stopRecording();
      // 可以在这里添加录音结束的提示，例如弹窗或状态更新
    }
  }, [countdown, isRecording]);

  return (
    <div>
        {autoStart && <p>剩余时间: {countdown} 秒</p>}
        {audioUrl && <audio src={audioUrl} controls />}
        {error && <p>Error: {error}</p>}
    </div>
);
}

export default AudioRecorder;
