import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Dialog } from '@headlessui/react'
import { Bars3Icon, XMarkIcon } from '@heroicons/react/24/outline'

const navigation = [
  { name: 'Product', href: '#' },
  { name: 'Features', href: '#' },
  { name: 'Marketplace', href: '#' },
  { name: 'Company', href: '#' },
]

function Home() {


  const navigate = useNavigate();


  const handlePractice = () => {
    navigate("/practice");
  };

  const handleInterview = () => {
    navigate("/interview");
  };

  return (
    <div className="bg-white">

      <div className="text-center text-white">
        <h1 className="text-3xl font-semibold mb-2">面试陪练小助手</h1>
        <h3 className="text-lg mb-5">——助你顺利通过升学面试</h3>
        <p>🤩 我是24小时陪你练习面试的小助手!😄</p>
        <p>📚 历年真题和模拟题一站式准备，轻松考入心仪学校！🚀</p>
        <p className="mb-5">💪 快来展现你的潜力，开始练习吧！💪</p>
        <button
          className="px-4 py-2 bg-blue-500 text-white rounded-full mx-2"
          onClick={handlePractice}
        >
          练习模式
        </button>
        <button
          className="px-4 py-2 bg-red-500 text-white rounded-full mx-2"
          onClick={handleInterview}
        >
          面试模式
        </button>
      </div>
      <div className="lg:w-5/12">
            <img src="/interview.png" alt="Image 1" className="w-full h-auto" />
          </div>
    </div>
  );
}

export default Home;
