import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import QuestionDisplay from '../components/QuestionDisplay';
import AnswerSection from '../components/AnswerSection';
import GenerateSection from '../components/GenerateSection';
import ControlPanel from '../components/ControlPanel'; // 确保此组件正确导入
import { Button } from 'react-bootstrap'; // 确保此组件正确导入

function Practice() {
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [evaluation, setEvaluation] = useState('');
  const [isAnsweringAllowed, setIsAnsweringAllowed] = useState(true);
  const [ttsService, setTtsService] = useState('openai'); // 默认使用 OpenAI TTS
  const [sttService, setSttService] = useState('openai'); // 默认使用 OpenAI STT
  const [evaluationService, setEvaluationService] = useState('zhipuai'); // 默认使用 Zhipuai 评价
  const navigate = useNavigate();

  useEffect(() => {
    fetchQuestion();
  }, []);

  const fetchQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/get-question');
      if (response.data) {
        setCurrentQuestion(response.data);
        setIsAnsweringAllowed(true);
      } else {
        console.error('No question received');
      }
    } catch (error) {
      console.error('Error fetching question:', error);
    }
  };

  const handleAnswerSubmit = (submittedAnswer) => {
    setAnswer(submittedAnswer);
    setIsAnsweringAllowed(false);
  };

  const handleEvaluationGenerated = (generatedEvaluation) => {
    setEvaluation(generatedEvaluation);
  };

  const handleReset = () => {
    fetchQuestion();
  };

  const handleViewRecords = () => {
    navigate('/history');
  };
  const [showControlPanel, setShowControlPanel] = useState(false); // 新状态


  return (
    <div className="container mt-3">
      <h1 className="text-center mb-4">练习模式</h1>
      <Button variant="outline-primary" onClick={() => setShowOffcanvas(true)}>
        设置
      </Button>

      <Offcanvas show={showOffcanvas} onHide={() => setShowOffcanvas(false)} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>设置</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <ControlPanel 
            setTtsService={setTtsService} 
            setSttService={setSttService} 
            setEvaluationService={setEvaluationService} 
          />
        </Offcanvas.Body>
      </Offcanvas>
        <QuestionDisplay question={currentQuestion.question} ttsService={ttsService} />
      <AnswerSection onAnswerSubmit={handleAnswerSubmit} isAnsweringAllowed={isAnsweringAllowed} sttService={sttService} />
      {answer && !isAnsweringAllowed && <GenerateSection question={currentQuestion.question} answer={answer} onEvaluationGenerated={handleEvaluationGenerated} evaluationService={evaluationService} />}
      <div className="d-flex justify-content-between mt-3">
        <Button onClick={handleReset} variant="secondary">重置问题</Button>
        <Button onClick={handleViewRecords} variant="primary">查看历史记录</Button>
      </div>
      {evaluation && (
        <div className="mt-3">
          <h5>评价结果:</h5>
          <p>{evaluation}</p>
        </div>
      )}
    </div>
  );
}

export default Practice;
