// AnswerSection.jsx
import React, {  useState } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';
import { Button } from 'react-bootstrap';
import '../App.css';

function AnswerSection({ onAnswerSubmit,disabled, }) {
  const [transcript, setTranscript] = useState(''); // 定义 transcript 状态
  const [audioUrl, setAudioUrl] = useState(''); // 定义 audioUrl 状态

  const [isRecording, setIsRecording] = useState(true);
  const [showTextInput, setShowTextInput] = useState(false);
  // const [answer, setAnswer] = useState(''); // 正确声明 answer 状态和 setAnswer 函数


  // useEffect(() => {
  //   // 重置相关状态
  //   setTranscript('');
  //   setAudioUrl('');
  //   setAnswer('');
  //   // 如果还有其他需要重置的状态，也放在这里
  // }, [resetKey]);


    const handleAudioTranscriptReady = (transcript, audioUrl) => {
    //   setIsRecording(false); // 停止录音
      setTranscript(transcript); // 设置转写文本
      setAudioUrl(audioUrl); // 设置录音文件的 URL
      onAnswerSubmit(transcript); // 提交答案进行评价
  };

    const toggleAnswerMode = () => {
        setIsRecording(!isRecording);
    };


    return (
      <div className="d-flex align-items-center justify-content-center">
      {isRecording ? (
        <AudioRecorder onTranscriptReady={handleAudioTranscriptReady} autoStart={isRecording} />
      ) : (
        <div id="inputarea"><TextInput onTextSubmit={onAnswerSubmit} /></div>
      )}
      <Button variant="primary" onClick={toggleAnswerMode} disabled={disabled} className="ml-2">
        <i className={isRecording ? "fa-solid fa-keyboard" : "fa-solid fa-microphone"}></i>
        {isRecording ? ' 切换到文本' : ' 切换到语音'}
      </Button>
    </div>

    );
}
export default AnswerSection;
