// Practice.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // 导入 useNavigate 钩子

import QuestionDisplay from '../components/QuestionDisplay';
import AnswerSection from '../components/AnswerSection';
import GenerateSection from '../components/GenerateSection';

function Practice({ ttsService }) {
  const [currentQuestion, setCurrentQuestion] = useState({});
  const [answer, setAnswer] = useState('');
  const [evaluation, setEvaluation] = useState('');
  const [isAnsweringAllowed, setIsAnsweringAllowed] = useState(true);

  const navigate = useNavigate(); // 用于导航


  useEffect(() => {
    fetchQuestion();
  }, []);

  const fetchQuestion = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/get-question');
      if (response.data) {
        setCurrentQuestion(response.data);
      } else {
        console.error('No question received');
      }
    } catch (error) {
      console.error('Error fetching question:', error);
    }
  };

  const handleAnswerSubmit = (submittedAnswer) => {
    setAnswer(submittedAnswer);
    setEvaluation('');
    setIsAnsweringAllowed(false); // 禁止回答
  };

  const handleEvaluationGenerated = (generatedEvaluation) => {
    setEvaluation(generatedEvaluation);
  };

  const handleReset = () => {
    setIsAnsweringAllowed(true);
    setAnswer('');
    setEvaluation('');
  };


  const handleContinue = async () => {
    setIsAnsweringAllowed(true);
    setAnswer('');
    setEvaluation('');
    await fetchQuestion(); // 获取新的问题
  };

  const handleViewRecords = () => {
    navigate('/history'); // 导航到历史记录页面
  };

  return (
    <div className="bg-light">
      <h1 className="display-4">练习模式</h1>
      <QuestionDisplay question={currentQuestion.question} ttsService={ttsService} />
      <AnswerSection onAnswerSubmit={handleAnswerSubmit} isAnsweringAllowed={isAnsweringAllowed} reset={handleReset} />
      {answer && <GenerateSection question={currentQuestion.question} answer={answer} onEvaluationGenerated={handleEvaluationGenerated} />}
      <div>
        <button onClick={handleReset}>重新练习</button>
        <button onClick={handleContinue}>继续答题</button>
        <button onClick={handleViewRecords}>查看记录</button>
      </div>
    </div>
  );
}

export default Practice;
