import { Link } from 'react-router-dom';
import { Navbar, Nav, Container,Offcanvas, NavDropdown } from 'react-bootstrap';
import ControlPanel from './ControlPanel'; // 确保正确导入 ControlPanel 组件
import '../styles/navbar-styles.css'; // 引入外部的CSS文件

function CustomNavbar({  }) {
  const [showOffcanvas, setShowOffcanvas] = React.useState(false);
  return (
    <>
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">面试模拟App</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            {/* 其他导航链接... */}
          </Nav>
          <Button variant="outline-secondary" onClick={() => setShowOffcanvas(true)}>
            设置
          </Button>
        </Navbar.Collapse>
      </Navbar>

      <Offcanvas show={showOffcanvas} onHide={() => setShowOffcanvas(false)} placement="end">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>设置</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <ControlPanel 
            /* 传入 ControlPanel 所需的 props */
          />
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
}

