import React from 'react';
import { Form } from 'react-bootstrap';
import { useSettings } from './SettingsContext'; // 确保路径正确

function ControlPanel() {

const { ttsService, setTtsService, sttService, setSttService, evaluationService, setEvaluationService } = useSettings();

  return (
    <div className="control-panel">
      <Form>
        <Form.Group>
            <Form.Check 
              type="switch"
              id="mute-switch"
              label="静音模式"
              checked={isMuted}
              onChange={(e) => setIsMuted(e.target.checked)}
            />
          </Form.Group>
        <Form.Group>

              <Form.Label>文本转语音服务</Form.Label>
              <Form.Control 
                as="select" 
                value={ttsService}
                onChange={(e) => setTtsService(e.target.value)}
              >
                <option value="minimax">Minimax</option>
                <option value="openai">OpenAI</option>
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>语音转文本服务</Form.Label>
              <Form.Control 
                as="select" 
                value={sttService}
                onChange={(e) => setSttService(e.target.value)}
              >
                <option value="openai">OpenAI STT</option>
              </Form.Control>
            </Form.Group>
            <Form.Group>
              <Form.Label>生成评价服务</Form.Label>
              <Form.Control 
                as="select" 
                value={evaluationService}
                onChange={(e) => setEvaluationService(e.target.value)}
              >
                <option value="zhipuai">Zhipuai</option>
                <option value="openai">OpenAI</option>
              </Form.Control>
            </Form.Group>
          </Form>

    </div>
  );
  }

export default ControlPanel;
