import React from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Footer from '../components/Footer.jsx';
import Feature from '../components/Feature';


function Home() {
  const navigate = useNavigate();

  const handlePractice = () => {
    navigate("/practice");
  };

  const handleInterview = () => {
    navigate("/interview");
  };

  return (
    <div>
    <Container className="mt-5 pb-md-8">
      <Row className='row justify-content-center'>
        <Col lg={7} md={12}>
          <div className="text-center text-lg-left">
            <div class="col-12 col-md-10 text-center mb-5 mb-md-7">

            <h1 className="display-3 mb-5 mt-5">面试陪练小助手</h1>
            {/* <h3 className="lead">——助你顺利通过升学面试</h3> */}
            <p class="lead mb-4 mb-lg-5">🦁嗨!我是面小狮,你的面试小助理!</p>
            <p class="lead mb-4 mb-lg-5">自招考试快到了，我为你准备了各种面试题目和面试技巧.让我来随时随地陪你练习,助你轻松考入心仪学校！🚀<br/>快来展现你的潜力，开始练习吧！💪 </p>
            <div  className='mt-5'>
              <Button variant="primary" className="mr-2" onClick={handlePractice}>题目练习</Button>
              <Button variant="danger" onClick={handleInterview}>模拟面试</Button>
            </div>
            </div>
          </div>
        </Col>
        <Col lg={5} sm={12}>
          <img src="/interview.png" alt="Image 1" className="img-fluid mt-4" />
        </Col>
      </Row>
    </Container>
    <div className='mt-5'><Feature/></div>
    <div className='mt-5'><Footer /></div>
    </div>
  );


}

export default Home;
