// components/SettingsContext.jsx
import React, { createContext, useState, useContext } from 'react';

const SettingsContext = createContext();

export const useSettings = () => useContext(SettingsContext);

export const SettingsProvider = ({ children }) => {
  const [ttsService, setTtsService] = useState('openai');
  const [sttService, setSttService] = useState('openai');
  const [evaluationService, setEvaluationService] = useState('zhipuai');

  return (
    <SettingsContext.Provider value={{ ttsService, setTtsService, sttService, setSttService, evaluationService, setEvaluationService }}>
      {children}
    </SettingsContext.Provider>
  );
};
