import React from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';

function Home() {
  const navigate = useNavigate();

  const handlePractice = () => {
    navigate("/practice");
  };

  const handleInterview = () => {
    navigate("/interview");
  };

  return (
    <Container className="mt-5">
      <Row>
        <Col lg={7}>
          <div className="text-center text-lg-left">
            <h1 className="display-3">面试陪练小助手</h1>
            {/* <h3 className="lead">——助你顺利通过升学面试</h3> */}
            <p className=''>🤩 我是24小时陪你练习面试的小助手!😄</p>
            <p>📚 历年真题和模拟题一站式准备，轻松考入心仪学校！🚀</p>
            <p className="mb-5">💪 快来展现你的潜力，开始练习吧！💪</p>
            <Button
              variant="primary"
              className="mr-2"
              onClick={handlePractice}
            >
              题目练习
            </Button>
            <Button
              variant="danger"
              onClick={handleInterview}
            >
              模拟面试
            </Button>
          </div>
        </Col>
        <Col lg={5}>
          <img src="/interview.png" alt="Image 1" className="img-fluid" />
        </Col>
      </Row>
    </Container>
  );
}

export default Home;
