import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Switch, Link} from 'react-router-dom';
import Navbar from './components/Navbar';
import History from './pages/History';
import Home from './pages/Home';
import Practice from './pages/Practice';
import Interview from './pages/Interview';
import PracticePage from './pages/PracticePage';
import InterviewPage from './pages/InterviewPage';



import './App.css';
// import 'tailwindcss/tailwind.css';
import 'bootstrap/dist/css/bootstrap.min.css'; // 导入 Bootstrap 样式




function App() {
  const [ttsService, setTtsService] = useState('browser'); // 默认TTS服务

  return (
    <Router>
      <div className="App">
        <Navbar setTtsService={setTtsService} />
        <nav>
          <ul>
            <li>
              <Link to="/">首页</Link>
            </li>
            <li>
              <Link to="/practice">练习模式</Link>
            </li>
            <li>
              <Link to="/interview">面试模式</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          {/* <Route path="/practice" element={<Practice ttsService={ttsService} />} /> */}
          {/* <Route path="/interview" element={<Interview ttsService={ttsService} />} /> */}
          <Route path="/history" element={<History />} />
          <Route path="/practice" element={<PracticePage />} />
          <Route path="/interview" element={<InterviewPage />} />

        </Routes>

      </div>

    </Router>
  );
}

export default App;
