// AnswerSection.jsx
// import 'bootstrap/dist/css/bootstrap.min.css'; // 导入 Bootstrap 样式
import { Button, Modal } from 'react-bootstrap'
import React, { useState, useEffect } from 'react';
import AudioRecorder from './AudioRecorder';
import TextInput from './TextInput';

function AnswerSection({ onAnswerSubmit }) {
  // const [answer, setAnswer] = useState('');
  const [transcript, setTranscript] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [showTextInput, setShowTextInput] = useState(false);

  const handleAudioTranscriptReady = (text) => {
    setTranscript(text); // 存储转写文本
    setIsRecording(false); // 停止录音
    onAnswerSubmit(text); // 直接生成评价
    };

  const handleTextInputSubmit = (text) => {
    // setAnswer(text);
    setShowTextInput(false); // 关闭文本输入
    onAnswerSubmit(text); // 直接生成评价

  };


  // 切换到文本输入模式
  const switchToTextInput = () => {
    setShowTextInput(true);
    setIsRecording(false);
  };

  // 切换到语音输入模式
  const switchToAudioInput = () => {
    setShowTextInput(false);
    setIsRecording(true);
  };


  return (
    <div className="container mt-4">
      <Button variant="danger" className="mr-2" onClick={switchToAudioInput}><i className="fa-solid fa-microphone"></i></Button>
      <Button variant="primary" onClick={switchToTextInput}><i className="fa-solid fa-keyboard"></i></Button>
      {isRecording && <AudioRecorder onTranscriptReady={handleAudioTranscriptReady} />}
      {showTextInput && <TextInput onTextSubmit={onAnswerSubmit} />}
      {transcript && <p>{transcript}</p>}
    </div>
  );
}

export default AnswerSection;
